import { useEffect } from "react";
import {useParams} from "react-router-dom";

import PokemonContext from "../../context/pokemons";
import {useContext} from "react";

export default function PokeDetail(){
    const {id} = useParams();
    const {getPokemonDetail, pokemonDetail} = useContext(PokemonContext);

    // Cada que se cambie la pantalla o cada que cambie el id solicitar el detalle del pokemon

    useEffect(() => {
        getPokemonDetail(id).catch(null);
    }, []);

    console.log(pokemonDetail);

    return(
        <div>
            <p>{`Name: ${pokemonDetail?.name}`}</p>
            <p>{`Peso: ${pokemonDetail?.weight}`}</p>
            <p>{`Altura: ${pokemonDetail?.height}`}</p>
        </div>
    );
}
